public static display Description255 showBUDescription(AssetTable _this)
{
    if (_this.HMBuildingUnit)
    {
        return  HMBuildingUnit::find(_this.HMBuildingUnit).Description + "(" + HMBuildingUnit::find(_this.HMBuildingUnit).Code + ")";
    }
    else
    {
        return "";
    }

}
