[PostHandlerFor(formStr(ContactPersonLookup), formMethodStr(ContactPersonLookup, init))]
public static void ContactPersonLookup_Post_init(XppPrePostArgs args)
{
    FormRun Sender = args.getThis();
    FormDataSource HMContactPersonRole_ds = sender.dataSource(FormDataSourceStr(ContactPersonLookup,HMContactPersonRole));
    FormDataSource LogisticsLocationRole_ds = sender.dataSource(FormDataSourcestr( ContactPersonLookup,LogisticsLocationRole));
    if(Sender.args().parm() == 'FilterRole')
    {

        LogisticsLocationRole_ds .queryBuildDataSource().addRange(fieldNum(LogisticsLocationRole,Type)).value(SysQuery::value(Sender.args().parmEnum()));
    }
    else
    {
       HMContactPersonRole_ds.queryBuildDataSource().enabled(false);
    }
}
