[SysClientCacheDataMethodAttribute(true)]
public static display str showPurpose(ContactPerson _this)
{
    HMContactPersonRole     contactPersonRole;
    LogisticsLocationRole   logisticsLocationRole;
    str                     role;
    while select Name from logisticsLocationRole
    join contactPersonRole
    where contactPersonRole.RecId == logisticsLocationRole.RecId
    && contactPersonRole.ContactPerson == _this.ContactPersonId
    {
        role += strFmt('%1; ',logisticsLocationRole.Name);
    }
    return role;
}

