public client static void lookupCustContactPersonHM(ContactPerson _this ,FormStringControl   _ctrl,
                                              CustAccount         _custAccount,
                                              FormStringControl   nameControl               = null,
                                                ContactPersonId     currentContactPersonId    = '',
                                              LogisticsLocationRoleType _logisticsLocationRoleType = LogisticsLocationRoleType::None)

{
    Args    args;
    Object  formRun;
    ;

    args = new Args();
    args.name(formStr(ContactPersonLookup));
    args.caller(_ctrl);
    args.lookupValue(currentContactPersonId);
    if(_logisticsLocationRoleType != LogisticsLocationRoleType::None)
    {
        args.parmEnum(_logisticsLocationRoleType);
        args.parm('FilterRole');
    }

    formRun = classfactory.formRunClass(args);
    formRun.custAccount(_custAccount);
    formRun.callingNameControl(nameControl);
    formRun.init();

    _ctrl.performFormLookup(formRun);
}
