public void updateCustAccountStatementExtTmp()
{
    CustAccountStatementExtTmp  custAccounttStatementExtTmpLocal;

    select forUpdate custAccounttStatementExtTmpLocal
        where custAccounttStatementExtTmpLocal.RecId == custAccountStatementExtTmp.RecId;

    if(custAccounttStatementExtTmpLocal.RecId)
    {
        ttsbegin;
        custAccounttStatementExtTmpLocal.HMAddmisionDate = hmCase.AddmisionDate;
        custAccounttStatementExtTmpLocal.HMDischargeDate = hmCase.DischargeDate;
        custAccounttStatementExtTmpLocal.HMVisitNum      = hmCase.CaseNo;
        custAccounttStatementExtTmpLocal.update();
        ttscommit;
    }

}
