public static LedgerDimensionDefaultAccount writeOffLedgerDimension(CustLedgerAccounts _this,
CustAccount         _custAccount,
CustPostingProfile  _custPostingProfile)
{
    LedgerDimensionDefaultAccount   writeOffLedgerDimension;
    CustPostingProfile              custPostingProfile = _custPostingProfile;

    if (custPostingProfile == '')
    {
        // posting profile id is blank so use the posting profile in the parameters table
        custPostingProfile = CustParameters::find().PostingProfile;
        if (custPostingProfile == '')
        {
            throw error("@SYS26844");
        }
    }

    writeOffLedgerDimension = any2int64(CustLedgerAccounts::findRefRecIdByPriority(_custAccount, custPostingProfile, fieldnum(CustLedgerAccounts, WriteOffLedgerDimension)));
    return writeOffLedgerDimension;
}
