[PreHandlerFor(tableStr(CustVendPaymJournalFee), tableMethodStr(CustVendPaymJournalFee, insert))]
public static void CustVendPaymJournalFee_Pre_insert(XppPrePostArgs args)
{
    CustVendPaymJournalFee _this = args.getThis();
    LedgerJournalTrans ledgerJournalTransSource = _this.sourceLedgerJournalTrans();  
      //<MazikCare>
    if(ledgerJournalTransSource.HMAppointmentNumber != '')
    {
        // If the payment fee company is the same as the ledgerJournalTrans.Company then sync with the
        // LedgerJournalTrans.DefaultDimension.
        // If the payment fee company is different than the LedgerJournalTrans.Company the payment fee dimension will be empty
        if (!_this.DefaultDimension && _this.company() == ledgerJournalTransSource.Company)
        {
            _this.DefaultDimension = ledgerJournalTransSource.DefaultDimension;
        }
    }


}

//<MazikCare>
public static void updateLedgerDimension(CustVendPaymJournalFee _this ,LedgerJournalTrans _ledgerJournalTrans)
{
    RecId mainAccountRecId;
    //todo: ax 7 to be resolved 
    //mainAccountRecId = DimensionStorage::getMainAccountIdFromLedgerDimension(_this.LedgerDimension);
    _this.LedgerDimension = HMDimensionClass::defaultDimension2ledgerDimension(MainAccount::find(mainAccountRecId).MainAccountId,_ledgerJournalTrans.DefaultDimension);
}
