Method.Source:
/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormDataSourceEventHandler(formDataSourceStr(CustTable, CustTable), FormDataSourceEventType::Initialized)]
public static void CustTable_OnInitialized(FormDataSource sender, FormDataSourceEventArgs e)
{
    FormDataSource fds = sender;
    formrun _formrun = fds.formRun();

    if(_formrun.args().menuItemName() == menuItemDisplayStr('HMEmployerListpage'))
    {
        fds.query().dataSourceTable(tablenum(custtable)).addRange(fieldNum(CustTable,CustType)).value(SysQuery::value(CustType::Employer));
    }
}
