/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[DataEventHandler(tableStr(DirOrganization), DataEventType::ValidatedWrite)]
public static void DirOrganization_onValidatedWrite(Common sender, DataEventArgs e)
{
    boolean ret;
    DirOrganization dirOrganization,_this;
    ValidateEventArgs Event = e as ValidateEventArgs;
    _this = sender as DirOrganization;
    ret = Event.parmValidateResult();
    if(ret)
    {
        select count(RecId) from dirOrganization
        where dirOrganization.OrgNumber != '' && dirOrganization.OrgNumber == _this.OrgNumber
        && dirOrganization.RecId != _this.RecId;

        if(dirOrganization.RecId > 0)
        {
            ret = false;
            warning("@MAZ13230");
        }

    }
    event.parmValidateResult(ret);


}
