/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormEventHandler(formStr(DirPartyQuickCreateForm), FormEventType::Initialized)]
public static void DirPartyQuickCreateForm_OnInitialized(xFormRun sender, FormEventArgs e)
{
    FormDesign          design = sender.design();
    FormComboBoxControl custType = design.controlName(formControlStr(DirPartyQuickCreateForm, CustType));

    if (sender.args().menuItemName() == menuitemDisplayStr(HMClientQuickCreate))
    {
        custType.selection(CustType::Client);
        design.caption(SysLabel::labelId2String2('New client'));
    }
    else if(sender.args().menuItemName() == menuitemDisplayStr(HMEmployerQuickCreate))
    {
        custType.selection(CustType::Employer);
        design.caption('Employer');
    }

}

/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormEventHandler(formStr(DirPartyQuickCreateForm), FormEventType::PostRun)]
public static void DirPartyQuickCreateForm_OnPostRun(xFormRun sender, FormEventArgs e)
{
    FormDesign          design = sender.design();

    if(sender.args().menuItemName() == menuitemDisplayStr(HMClientQuickCreate))
    {
       design.controlName(formControlStr(DirPartyQuickCreateForm, partyTypeComboBox)).visible(false);
    }
}

/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormDataSourceEventHandler(formDataSourceStr(DirPartyQuickCreateForm, DirPartyEntity), FormDataSourceEventType::PostLinkActive)]
public static void DirPartyEntity_OnPostLinkActive(FormDataSource sender, FormDataSourceEventArgs e)
{
    FormRun                 formRun = sender.formRun();
    FormComboBoxControl     partyTypeComboBox =  formRun.design().controlName(formControlStr(DirPartyQuickCreateForm, partyTypeComboBox));
    if(formRun.args().menuItemName() == menuitemDisplayStr(HMClientQuickCreate))
    {
        partyTypeComboBox.selection(DirPartyType::Organization);
        partyTypeComboBox.modified();
    }
}
