public static display Name getFullArabicName(DirPerson _this)
{
    DirPersonName personName;

    select personName
    where PersonName.Person == _this.RecId;

    return personName.HMArabicFirstName + ' ' + personName.HMArabicMiddleName + ' ' + personName.HMArabicLastName + ' ' + personName.HMArabicFamilyName;
}

public static display Name getFullName(DirPerson _this)
{
    DirPersonName personName;

    select personName
    where PersonName.Person == _this.RecId;

    return personName.FirstName + ' ' + personName.MiddleName + ' ' + personName.LastName + ' ' + personName.HMFamilyName;
}
