private void updateMazikCareField(str _calledFrom)
{
    switch(_calledFrom)
    {
        case 'HCPCS':
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,CopyOfHMEcoResProductCreateReleaseHCPCS),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleaseHCPCS2),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,EcoResProductCreate),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,MazikCareProduct),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleaseHCPCS),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMAdjunctProcedures),true);
            break;
        case 'Medication':
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleaseMedication2),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,EcoResProductCreate),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,MazikCareProduct),false);
            break;
        case 'Laboratory':
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleaseLab2),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,EcoResProductCreate),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,MazikCareProduct),false);
            break;
        case 'Radiology':
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleaseRadiology2),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,EcoResProductCreate),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,MazikCareProduct),false);
            break;
        case 'Physiotherapy':
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleasePhysioHCPCS2),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,EcoResProductCreate),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,MazikCareProduct),false);
            break;
        case 'CSSD':
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,HMEcoResProductCreateReleaseCSSD),true);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,EcoResProductCreate),false);
            this.listPage().actionPaneControlVisible(formControlStr(EcoResProductPerCompanyListPage,MazikCareProduct),false);
    }
}
