public static void main(Args _args)
{
    SalesLine salesLine;
    InventDim inventDim;

    while select salesLine where salesLine.inventDimId != ""
        join inventDim where inventDim.inventDimId == salesLine.InventDimId
        && inventDim.inventSerialId != ""
    {
        info(strfmt("%1 _|__ %2 _|_ %3", salesLine.SalesId, salesLine.ItemId, inventDim.inventSerialId));
    }

}
