public void selectionChanged()
{
    HMAppointmentEODStatement    HMAppointmentEODStatement;
    super();

    HMAppointmentEODStatement = this.listPage().activeRecord('HMAppointmentEODStatement');

    if(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Open)
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_CalcStmt), true);
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_PostStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_ClearStmt), false);
    }
    else if(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Calculated)
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_CalcStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_PostStmt), true);
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_ClearStmt), true);
    }
    else if(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Posted)
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_CalcStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_PostStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMAppointmentEODStatementListPage,HMAppointmentEODStatementClass_ClearStmt), false);
    }
}
