protected void preRunModifyContract()
{
    //define object for report contract
    HMAppointmentEworksheetDataContract contract;
    HMResourceSchedulingAppointment     hmResourceSchedulingAppointment;
    FormDataSource              fds;
    List apptList = new List(Types::Int64);

    //get the reference of the current contract object
    contract = this.parmReportContract().parmRdpContract() as HMAppointmentEworksheetDataContract;

    //modify the parameter value of the contract
    fds = FormDataUtil::getFormDataSource(this.parmArgs().record());

    for (hmResourceSchedulingAppointment = fds.getFirst(true) ? fds.getFirst(true) : fds.cursor(); hmResourceSchedulingAppointment; hmResourceSchedulingAppointment = fds.getNext())
    {
        apptList.addEnd(hmResourceSchedulingAppointment.RecId);
    }

    contract.parmAppointmentRecId(apptList);
}

public static client void main(Args args)
{
    //define the new object for controller class
    HMAppointmentEWorksheetController hmAppointmentEWorksheetController;

    hmAppointmentEWorksheetController = new HMAppointmentEWorksheetController();

    //pass the caller args to the controller
    hmAppointmentEWorksheetController.parmArgs(args);
    hmAppointmentEWorksheetController.parmShowDialog(false);

    //set the report name and report design to run
    hmAppointmentEWorksheetController.parmReportName(ssrsReportStr(HMAppointmentEWorksheet,ReportDesign));

    //execute the report
    hmAppointmentEWorksheetController.startOperation();
}
