Method.Source:
/// <summary>
/// Construct the class
/// </summary>
/// <returns>Return the class object</returns>
public server static HMCalculateClaimPoolMultiThreadBatchJob construct()
{
    return new HMCalculateClaimPoolMultiThreadBatchJob();
}

/// <summary>
/// 
/// </summary>
public void run()
{
    HMCalculateClaimPoolMultiThreadTask hmCalculateClaimPoolMultiThreadTask;

    try
    {
        if(this.isInBatch())
        {
            for(int i = 1; i <= numberOfThreads; i++)
            {
                if(!batchHeader)
                {
                    batchHeader = BatchHeader::construct(this.parmCurrentBatch().BatchJobId);
                }

                hmCalculateClaimPoolMultiThreadTask = HMCalculateClaimPoolMultiThreadTask::construct();
                hmCalculateClaimPoolMultiThreadTask.parmTaskDescription(strFmt("@MAZ:AssignmentOfPoolsOnClaimsMultipleThreadTask", i));
                hmCalculateClaimPoolMultiThreadTask.parmTaskNumber(i);
                hmCalculateClaimPoolMultiThreadTask.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);
                hmCalculateClaimPoolMultiThreadTask.parmHMClaimBatchJobLogsHeaderDataPrep(hmClaimBatchJobLogsHeaderDataPrep);

                // add tasks to the batch header
                batchHeader.addRuntimeTask(hmCalculateClaimPoolMultiThreadTask, this.parmCurrentBatch().RecId);
            }

        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();//BatchStatus::Hold);
        }

    }
    catch
    {
        throw Error(strFmt("@MAZ:PoolAssignmentBatchJobFailed", xSession::xppCallStack()));
    }

}

//Stores parameters of the batch
public container pack()
{
    return [#CurrentVersion] + [#NumberOfThreads] + [#TaskDescription] + [#HMClaimBatchJobLogsHeader] + [#HMClaimBatchJobLogsHeaderDataPrep];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret     = true;
    int         version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version, #NumberOfThreads, #TaskDescription, #HMClaimBatchJobLogsHeader, #HMClaimBatchJobLogsHeaderDataPrep] = packedClass;
            break;
        default:
            ret = false;
    }

    return ret;
}
