public static void HMCaseInsuranceActiveUpdation(RecId _hmcaseinsurance)
{
   HMCaseInsurance hmcaseins;

   select forUpdate hmcaseins where hmcaseins.RecId==_hmcaseinsurance;
   if(hmcaseins)
    {
        ttsBegin;
        hmcaseins.IsActive=0;
        hmcaseins.update();
        ttsCommit;
    }
}
