public void selectionChanged()
{
    HMCase   hmCase;
    super();

    hmCase = this.listPage().activeRecord('HMCase_1');

    this.listPage().actionPaneControlEnabled(formControlStr(HMCaseListPage,
    HMCaseFinancialDischarge),hmCase.CaseStatus == HMCaseStatus::ClinicalDischarge);

    if(HMPatientEODStatementClass::userHasOpenStatement(curUserId()))
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMCaseListPage,HMPatientEODStatementStartShift),false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMCaseListPage,HMPatientEODStatementEndShift),true);
    }
    else
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMCaseListPage,HMPatientEODStatementStartShift),true);
        this.listPage().actionPaneControlEnabled(formControlStr(HMCaseListPage,HMPatientEODStatementEndShift),false);
    }

}
