public static void main(Args _args)
{
    #Task
    HMCase hMCase;
    if(_args.menuItemName() == menuitemActionStr(HMCaseStatusOnHold))
    {
        new MenuFunction(MenuItemDisplayStr(HMCaseHold),MenuItemType::Display).run(_args);
        //ttsBegin;
        //select forUpdate hMCase
            //where hMCase.RecId==_args.record().RecId;
        //hMCase.CaseStatus=HMCaseStatus::OnHold;
        //hMCase.update();
        //ttsCommit;
    }
    else if(_args.menuItemName() == menuitemActionStr(HMCaseStatusOpen))
    {
        ttsBegin;
        select forUpdate hMCase
            where hMCase.RecId==_args.record().RecId;
        hMCase.CaseStatus=HMCaseStatus::Open;
        hMCase.HMCaseReason=0;
        hMCase.update();
        ttsCommit;
    }
    else if (_args.menuItemName() == menuitemActionStr(HMCaseStatusClosed))
    {
        ttsBegin;
        select forUpdate hMCase
            where hMCase.RecId==_args.record().RecId;
        hMCase.CaseStatus=HMCaseStatus::Closed;
        hMCase.update();
        ttsCommit;
        info("Case closed successfully");
    }
    if(_args.parm() == 'RefreshQuery')
    {
        _args.caller().task(#taskRefresh);
    }

}
