/// <summary>
/// 
/// </summary>
public void run()
{
    HMDataPrepForClaimAgeCalcMultiThreadBatchJob    hmDataPrepForClaimAgeCalcMultiThreadBatchJob;
    HMClaimAgeCalculateMultiThreadBatchJob          hmClaimAgeCalculateMultiThreadBatchJob;
    int                                             recordsToProcess, perThreadRecordsRound, additionalRecordsRound;
    HMOneDecimal                                    additionalRecords;
    real                                            perThreadRecords, diffPerThread;
    RecId                                           hmClaimBatchJobLogsHeader, hmClaimBatchJobLogsHeaderDataPrep, batchJobId;
    HMClaimQualifiedForAgeCalculate                 hmClaimQualifiedForAgeCalculate;

    try
    {
        ttsbegin;

        delete_from hmClaimQualifiedForAgeCalculate
            where hmClaimQualifiedForAgeCalculate.CreatedDateTime <= DateTimeUtil::addDays(DateTimeUtil::utcNow(), -2);;

        ttscommit;
    }
    catch (Exception::Deadlock)
    {
        if (xSession::currentRetryCount() >= #RetryNum)
        {
            throw error(strFmt("@MAZ:FailedToDeleteDataFromTmpTable", "HMClaimQualifiedForAgeCalculate"));
        }
        else
        {
            retry;
        }

    }
    catch
    {
        if (xSession::currentRetryCount() >= #RetryNum)
        {
            throw error(strFmt("@MAZ:FailedToDeleteDataFromTmpTable", "HMClaimQualifiedForAgeCalculate"));
        }
        else
        {
            retry;
        }
    }

    try
    {

        if(this.isInBatch())
        {
            numberOfThreads = numberOfThreads == 0 ? 1 : numberOfThreads < 0 ? numberOfThreads * -1 : numberOfThreads;

            recordsToProcess = SysQuery::countTotal(gQueryRun);

            Info(strFmt("@MAZ:NoOfRecordsPicked", recordsToProcess));

            if(recordsToProcess <= numberOfThreads)
            {
                numberOfThreads = 1;

                Info("@MAZ:RecordsLesserThanNumberOfThreads");
            }

            perThreadRecords        = recordsToProcess / numberOfThreads;
            perThreadRecordsRound   = perThreadRecords;
            diffPerThread           = perThreadRecords - perThreadRecordsRound;
            additionalRecords       = diffPerThread * numberOfThreads;
            additionalRecords       = roundUp(additionalRecords, 1);
            additionalRecordsRound  = additionalRecords;

            if(perThreadRecordsRound + additionalRecordsRound > 0)
            {
                if(!batchHeader)
                {
                    batchJobId = this.parmCurrentBatch().BatchJobId;

                    batchHeader = BatchHeader::construct(batchJobId);

                    batchHeader.parmRetriesOnFailure(5);
                }

                hmDataPrepForClaimAgeCalcMultiThreadBatchJob = HMDataPrepForClaimAgeCalcMultiThreadBatchJob::construct();
                hmDataPrepForClaimAgeCalcMultiThreadBatchJob.parmQueryRun(gQueryRun);
                hmDataPrepForClaimAgeCalcMultiThreadBatchJob.parmNumberOfThreads(numberOfThreads);
                hmDataPrepForClaimAgeCalcMultiThreadBatchJob.parmPerThreadRecordsRound(perThreadRecordsRound);
                hmDataPrepForClaimAgeCalcMultiThreadBatchJob.parmAdditionalRecords(additionalRecordsRound);
                hmDataPrepForClaimAgeCalcMultiThreadBatchJob.parmTaskDescription("@MAZ:DataPreparation");

                hmClaimBatchJobLogsHeaderDataPrep = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::AgeDataPrep
                                                                                , batchJobId
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmDataPrepForClaimAgeCalcMultiThreadBatchJob.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeaderDataPrep);


                hmClaimAgeCalculateMultiThreadBatchJob = HMClaimAgeCalculateMultiThreadBatchJob::construct();
                hmClaimAgeCalculateMultiThreadBatchJob.parmNumberOfThreads(numberOfThreads);
                hmClaimAgeCalculateMultiThreadBatchJob.parmTaskDescription("@MAZ:AssignmentOfAgeOnClaims");
                hmClaimAgeCalculateMultiThreadBatchJob.parmHMClaimBatchJobLogsHeaderDataPrep(hmClaimBatchJobLogsHeaderDataPrep);

                hmClaimBatchJobLogsHeader = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::Age
                                                                                , batchJobId
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmClaimAgeCalculateMultiThreadBatchJob.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);

                batchHeader.addRuntimeTask(hmDataPrepForClaimAgeCalcMultiThreadBatchJob, this.parmCurrentBatch().RecId);
                batchHeader.addRuntimeTask(hmClaimAgeCalculateMultiThreadBatchJob, this.parmCurrentBatch().RecId);

                batchHeader.addDependency(hmClaimAgeCalculateMultiThreadBatchJob, hmDataPrepForClaimAgeCalcMultiThreadBatchJob);

            }
            else
            {
                Info("@MAZ:NoRecordsFoundForProcessing");
            }

        }
        else
        {
            // execute your code here when not running in batch
            try
            {
                HMDataPrepForClaimAgeCalcBusinessLogic  hmDataPrepForClaimAgeCalcBusinessLogic = HMDataPrepForClaimAgeCalcBusinessLogic::construct();
                HMClaimAgeCalculateBusinessLogic        hmClaimAgeCalculateBusinessLogic = HMClaimAgeCalculateBusinessLogic::construct();

                hmClaimBatchJobLogsHeaderDataPrep = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::AgeDataPrep
                                                                                , 0
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmDataPrepForClaimAgeCalcBusinessLogic.processInit(gQueryRun, 0, hmClaimBatchJobLogsHeaderDataPrep);

                hmClaimBatchJobLogsHeader = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::Age
                                                                                , 0
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmClaimAgeCalculateBusinessLogic.processInit(0, hmClaimBatchJobLogsHeader, hmClaimBatchJobLogsHeaderDataPrep);
            }
            catch
            {
                warning(strFmt("%1",xSession::xppCallStack()));
            }
        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();//BatchStatus::Hold);
        }

    }
    catch
    {
        throw error(strFmt("@MAZ:BatchJobInitiationFailed",xSession::xppCallStack()));
    }
}

//Needs to be set to true so class can be ran in a batch
boolean canGoBatch()
{
    return true;
}

//Stores parameters of the batch
public container pack()
{
    container pack = conNull();
    if (gQueryRun)
    {
        pack = gQueryRun.pack();
    }
    return [#CurrentVersion] + [pack] + [#CurrentList];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret         = false;
    int         version     = RunBase::getVersion(packedClass);
    container   packedQuery = conNull();

    switch (version)
    {
        case #CurrentVersion:
            [version, packedQuery, #CurrentList] = packedClass;

            if (SysQuery::isPackedOk(packedQuery))
            {
                gQueryRun   = new QueryRun(packedQuery);
                ret         = true;
            }
            break;

        default:
            ret = false;
    }
    return ret;
}

//Determines whether to run on server or client
//True - Server; False - Client
public boolean runsImpersonated()
{
    return true;
}

/// <summary>
/// 
/// </summary>
/// <param name = "args"></param>
public static void main(Args args)
{
    HMClaimAgeCalculateMainMultiThreadBatchJob hmClaimAgeCalculateMainMultiThreadBatchJob = HMClaimAgeCalculateMainMultiThreadBatchJob::construct();

    hmClaimAgeCalculateMainMultiThreadBatchJob.caption();
    hmClaimAgeCalculateMainMultiThreadBatchJob.initQueryRun();

    if (hmClaimAgeCalculateMainMultiThreadBatchJob.prompt())
    {
        hmClaimAgeCalculateMainMultiThreadBatchJob.run();
    }

}

