class HMClaimAgeCalculateMultiThreadBatchJob extends RunBaseBatch
{
    BatchHeader         batchHeader;
    HMNumberOfThreads   numberOfThreads;
    static str          taskDescription;
    RecId               hmClaimBatchJobLogsHeader, hmClaimBatchJobLogsHeaderDataPrep;

    #define.CurrentVersion(1)
    #localmacro.NumberOfThreads
        numberOfThreads
    #endmacro
    #LOCALMACRO.TaskDescription
        taskDescription
    #ENDMACRO
    #LOCALMACRO.HMClaimBatchJobLogsHeader
        hmClaimBatchJobLogsHeader
    #ENDMACRO
    #LOCALMACRO.HMClaimBatchJobLogsHeaderDataPrep
        hmClaimBatchJobLogsHeaderDataPrep
    #ENDMACRO

}

Method.Source:
/// <summary>
/// Construct the class
/// </summary>
/// <returns>Return the class object</returns>
public server static HMClaimAgeCalculateMultiThreadBatchJob construct()
{
    return new HMClaimAgeCalculateMultiThreadBatchJob();
}

/// <summary>
/// 
/// </summary>
public void run()
{
    HMClaimAgeCalculateMultiThreadTask  hmClaimAgeCalculateMultiThreadTask;

    try
    {
        if(this.isInBatch())
        {

            for(int i = 1; i <= numberOfThreads; i++)
            {
                if(!batchHeader)
                {
                    batchHeader = BatchHeader::construct(this.parmCurrentBatch().BatchJobId);
                }

                hmClaimAgeCalculateMultiThreadTask = HMClaimAgeCalculateMultiThreadTask::construct();
                hmClaimAgeCalculateMultiThreadTask.parmTaskDescription(strFmt("@MAZ:AssignmentOfAgeOnClaimsMultipleThreadTask", i));
                hmClaimAgeCalculateMultiThreadTask.parmTaskNumber(i);
                hmClaimAgeCalculateMultiThreadTask.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);
                hmClaimAgeCalculateMultiThreadTask.parmHMClaimBatchJobLogsHeaderDataPrep(hmClaimBatchJobLogsHeaderDataPrep);

                // add tasks to the batch header
                batchHeader.addRuntimeTask(hmClaimAgeCalculateMultiThreadTask, this.parmCurrentBatch().RecId);
            }

        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();//BatchStatus::Hold);
        }

    }
    catch
    {
        throw error(strFmt("@MAZ:AgeAssignmentBatchJobFailed",xSession::xppCallStack()));
    }
}

//Stores parameters of the batch
 public container pack()
 {
     return [#CurrentVersion] + [#NumberOfThreads] + [#TaskDescription] + [#HMClaimBatchJobLogsHeader] + [#HMClaimBatchJobLogsHeaderDataPrep];
 }

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret     = true;
    int         version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version, #NumberOfThreads, #TaskDescription, #HMClaimBatchJobLogsHeader, #HMClaimBatchJobLogsHeaderDataPrep] = packedClass;
            break;
        default:
            ret = false;
    }

    return ret;
}
