private void BatchNumLookup(FormStringControl _control)
{
    SysTableLookup          sysTableLookup;
    QueryBuildDataSource    queryBuildDataSource,qbds1;
     QueryBuildRange         queryBuildRange;
    Query                   query = new Query();



    sysTableLookup = SysTableLookup::newParameters(tablenum(HMInsuranceCarrierBatchDetails), _control);
    sysTableLookup.addLookupfield(fieldnum(HMInsuranceCarrierBatchDetails, BatchNumber),  true);
    queryBuildDataSource = query.addDataSource(tablenum(HMInsuranceCarrierBatchDetails));
    qbds1=queryBuildDataSource.addDataSource(tableNum(HMInsuranceCarrier));
    qbds1.joinMode(JoinMode::InnerJoin);
    qbds1.addLink(fieldNum(HMInsuranceCarrierBatchDetails,HMInsuranceCarrier),fieldNum(HMInsuranceCarrier,RecId));
    queryBuildRange = qbds1.addRange(fieldNum(HMInsuranceCarrier, Code));
    queryBuildRange.value(DialogInsCode.value());

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

private void InsCodeLookup(FormStringControl _control)
{
    SysTableLookup          sysTableLookup;
    QueryBuildDataSource    queryBuildDataSource,qbds1,qbds2;
    QueryBuildRange         queryBuildRange;
    Query                   query = new Query();


    sysTableLookup = SysTableLookup::newParameters(tablenum(HMInsuranceCarrier), _control);
    sysTableLookup.addLookupfield(fieldnum(HMInsuranceCarrier, Code),  true);
    sysTableLookup.addLookupfield(fieldnum(HMInsuranceCarrier, Name));
    queryBuildDataSource = query.addDataSource(tablenum(HMInsuranceCarrier));

    if(DialogTpaCode.value())
    {
        qbds1=queryBuildDataSource.addDataSource(tableNum(HMTpaDetails));
        qbds1.joinMode(JoinMode::InnerJoin);
        qbds1.addLink(fieldNum(HMInsuranceCarrier,RecId),fieldNum(HMTpaDetails,HMInsuranceCarrier));

        qbds2=qbds1.addDataSource(tableNum(HMTpa));
        qbds2.joinMode(JoinMode::InnerJoin);
        qbds2.addLink(fieldNum(HMTpaDetails,Tpa),fieldNum(HMTPA,RecId));


        queryBuildRange = qbds2.addRange(fieldNum(HMTPA, Code));
        queryBuildRange.value(DialogTpaCode.value());
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void postRun()
{
    HMClaimPDFServiceDataContract  hMClaimPDFServiceDataContract;
    Dialog dialogLocal                    = this.dialog();

    super();
    //dialogFieldCategory     = this.bindInfo().getDialogField(this.dataContractObject(), methodStr(ReportContract, parmCategoryList));
    hMClaimPDFServiceDataContract = this.dataContractObject();
    DialogBatchNum=this.bindInfo().getDialogField(hMClaimPDFServiceDataContract,methodStr(hMClaimPDFServiceDataContract,parmBatchNumber));
    DialogBatchNum.registerOverrideMethod(methodStr(FormStringControl,lookup),methodStr(HMClaimPDFServiceUIBuilder,BatchNumLookup),this);

    DialogTpaCode=this.bindInfo().getDialogField(hMClaimPDFServiceDataContract,methodStr(hMClaimPDFServiceDataContract,parmTpaCode));
    DialogTpaCode.registerOverrideMethod(methodStr(FormStringControl,lookup),methodStr(HMClaimPDFServiceUIBuilder,TpaCodeLookup),this);

    DialogInsCode=this.bindInfo().getDialogField(hMClaimPDFServiceDataContract,methodStr(hMClaimPDFServiceDataContract,parmInsuranceCode));
    DialogInsCode.registerOverrideMethod(methodStr(FormStringControl,lookup),methodStr(HMClaimPDFServiceUIBuilder,InsCodeLookup),this);


}

private void TpaCodeLookup(FormStringControl _control)
{
    SysTableLookup          sysTableLookup;
    QueryBuildDataSource    queryBuildDataSource;
    Query                   query = new Query();

    queryBuildDataSource = query.addDataSource(tablenum(HMTPA));

    sysTableLookup = SysTableLookup::newParameters(tablenum(HMTPA), _control);
    sysTableLookup.addLookupfield(fieldnum(HMTPA, Code),  true);
    sysTableLookup.addLookupfield(fieldnum(HMTPA, Name));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}
