/// <summary>
///    Finds the person assigned to the current user.
/// </summary>
/// <returns>
///    The ID of the person assigned to current user.
/// </returns>
[QueryRangeFunctionAttribute()]
public static RecId getCurrentAgent()
{
    CustCollectionsAgent custCollectionsAgent = CustCollectionsAgent::findCurrentAgent();
    return custCollectionsAgent.RecId; 
}

/// <summary>
///    Finds the pools of person assigned to the current user.
/// </summary>
/// <returns>
///    The ID of the pools of person assigned to current user.
/// </returns>
[QueryRangeFunctionAttribute()]
public static str getPersonPools()
{
    CustCollectionsPool         mzkCustCollectionsPool;
    CustCollectionsAgentPool    mzkcustCollectionsAgentPool;
    CustCollectionsAgent        custCollectionsAgent;
    str                         poolsIdList;

    custCollectionsAgent = CustCollectionsAgent::findCurrentAgent();

    while select RecId from mzkCustCollectionsPool
        join mzkcustCollectionsAgentPool
        where(mzkCustCollectionsPool.Name == mzkcustCollectionsAgentPool.CustomerPoolId)
        && mzkcustCollectionsAgentPool.CustCollectionsAgent == custCollectionsAgent.RecId
    {
        poolsIdList += strFmt("%1,",mzkCustCollectionsPool.RecId);
    }

    return poolsIdList;
}

/// <summary>
///    Finds the pools of person assigned to the current user.
/// </summary>
/// <returns>
///    The ID of the pools of person assigned to current user.
/// </returns>
[QueryRangeFunctionAttribute()]
public static utcdatetime getCurrentDate()
{
    return DateTimeUtil::utcNow();
}

/// <summary>
///    Finds the pools of person assigned to the current user.
/// </summary>
/// <returns>
///    The ID of the pools of person assigned to current user.
/// </returns>
[QueryRangeFunctionAttribute()]
public static utcdatetime getDifferenceDate()
{
    return DateTimeUtil::addDays(DateTimeUtil::utcNow(),-7);
}

/// <summary>
///    Finds the OMTeam
/// </summary>
/// <returns>
///    The ID of OMTeam
/// </returns>
[QueryRangeFunctionAttribute()]
public static RecId getOMTeamRecId()
{
    return CustCollectionsAgent::collectionsTeam().RecId;
}
