public static void main(Args args)
{
    HMDailyCensusReportController hmDailyCensusReportController;

    hmDailyCensusReportController = new HMDailyCensusReportController();

    //pass the caller args to the controller
    hmDailyCensusReportController.parmArgs(args);
    hmDailyCensusReportController.parmReportName(ssrsReportStr(HMDailyCensusReport, Report));

    //execute the report
    hmDailyCensusReportController.parmShowDialog(false);
    hmDailyCensusReportController.startOperation();
}
