Method.Source:
    /// <summary>
/// Construct the class
/// </summary>
/// <returns>Return the class object</returns>
public server static HMDataPrepForCalcClaimPoolMultiThreadBatchJob construct()
{
    return new HMDataPrepForCalcClaimPoolMultiThreadBatchJob();
}

/// <summary>
/// 
/// </summary>
public void run()
{
    HMDataPrepForCalcClaimPoolMultiThreadTask   hmDataPrepForCalcClaimPoolMultiThreadTask;
    int                                         queryStartPosition, queryEndPosition;

    try
    {
        if(this.isInBatch())
        {
            for(int i = 1; i <= numberOfThreads; i++)
            {
                if(!batchHeader)
                {
                    batchHeader = BatchHeader::construct(this.parmCurrentBatch().BatchJobId);
                }

                if(i == 1)
                {
                    queryStartPosition  = 1;
                    queryEndPosition    = perThreadRecordsRound + additionalRecords;
                }
                else
                {
                    queryStartPosition  = queryEndPosition + 1;
                    queryEndPosition    = queryEndPosition + perThreadRecordsRound;
                }

                hmDataPrepForCalcClaimPoolMultiThreadTask = HMDataPrepForCalcClaimPoolMultiThreadTask::construct();
                hmDataPrepForCalcClaimPoolMultiThreadTask.parmQueryRun(gQueryRun);
                hmDataPrepForCalcClaimPoolMultiThreadTask.parmQueryStartPosition(queryStartPosition);
                hmDataPrepForCalcClaimPoolMultiThreadTask.parmPerThreadRecords(i == 1 ? perThreadRecordsRound + additionalRecords : perThreadRecordsRound);
                hmDataPrepForCalcClaimPoolMultiThreadTask.parmTaskDescription(strFmt("@MAZ:DataPreparationForClaimPoolMultipleThreadTask", i, queryStartPosition, queryEndPosition));
                hmDataPrepForCalcClaimPoolMultiThreadTask.parmTaskNumber(i);
                hmDataPrepForCalcClaimPoolMultiThreadTask.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);

                // add tasks to the batch header
                batchHeader.addRuntimeTask(hmDataPrepForCalcClaimPoolMultiThreadTask, this.parmCurrentBatch().RecId);

            }

        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();//BatchStatus::Hold);
        }

    }
    catch
    {
        throw error(strFmt('@MAZ:DataPreparationBatchJobFailedError',xSession::xppCallStack()));
    }
}

//Stores parameters of the batch
public container pack()
{
    container pack = conNull();

    if (gQueryRun)
    {
        pack = gQueryRun.pack();
    }

    return [#CurrentVersion] + [pack] + [#NumberOfThreads] + [#PerThreadRecordsRound] + [#AdditionalRecords] + [#TaskDescription] + [#HMClaimBatchJobLogsHeader];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret         = false;
    int         version     = RunBase::getVersion(packedClass);
    container   packedQuery = conNull();

    switch (version)
    {
        case #CurrentVersion:
            [version, packedQuery, #NumberOfThreads, #PerThreadRecordsRound, #AdditionalRecords, #TaskDescription, #HMClaimBatchJobLogsHeader] = packedClass;

            if (SysQuery::isPackedOk(packedQuery))
            {
                gQueryRun   = new QueryRun(packedQuery);
                ret         = true;
            }
            break;
        default:
            ret = false;
    }

    return ret;
}
