/// <summary>
/// Creates a new instance of the <c>HMDirViewLocationHierarchyController</c> class.
/// </summary>
/// <param name="_ui">
/// An instance of the UI interface.
/// </param>
/// <returns>
/// A new instance of the <c>HMDirViewLocationHierarchyController</c> class.
/// </returns>
public static HMDirViewLocationHierarchyController construct(DirViewLocationHierarchyUIInterface _ui)
{
    HMDirViewLocationHierarchyController  controller = new HMDirViewLocationHierarchyController();
    controller.parmUI(_ui);
    controller.init();
    return controller;
}
