public static void main(Args args)
{
    HMEMGInvoiceController controller = new HMEMGInvoiceController();

    if (args.record().TableId == tableNum(HMCase) &&
        args.record().RecId != 0)
    {
        controller.parmArgs(args);
        controller.parmReportName(ssrsReportStr(HMEMGInvoiceReport, EMGInvoiceReport));
        controller.parmShowDialog(false);
        controller.startOperation();
    }
}

/// <summary>
///
/// </summary>
protected void preRunModifyContract()
{
    HMEMGInvoiceContract    contract;
    HMCase                  hmCase;

    contract = this.parmReportContract().parmRdpContract() as HMEMGInvoiceContract;
    hmCase = HMCase::find(this.parmArgs().record().RecId);

    super();
    contract.parmHMCaseRecId(hmCase.RecId);
}
