public static void createEventLog(RefTableId _refTableId,HMEventType _hmEventType,RefRecId _refRecId = 0)
{
    HMEventLog hmEventLog;

    hmEventLog.RefTableId  = _refTableId;
    hmEventLog.RefRecId    = _refRecId;
    hmEventLog.HMEventType = _hmEventType;
    hmEventLog.StartDateTime = DateTimeUtil::getSystemDateTime();
    hmEventLog.insert();

}

public static void updateEventLog(RefTableId _refTableId,HMEventType _hmEventType,RefRecId _refRecId = 0)
{
    HMEventLog hmEventLog;

    ttsBegin;

    select forUpdate hmEventLog
        where hmEventLog.RefTableId == _refTableId && hmEventLog.RefRecId == _refRecId && hmEventLog.HMEventType == _hmEventType;
    if (hmEventLog)
    {
        hmEventLog.EndDateTime = DateTimeUtil::getSystemDateTime();
        hmEventLog.update();
    }
    else
    {
        select forUpdate hmEventLog
            where hmEventLog.RefTableId == _refTableId && hmEventLog.HMEventType == _hmEventType;
        if (hmEventLog)
        {
            hmEventLog.RefRecId    = _refRecId;
            hmEventLog.EndDateTime = DateTimeUtil::getSystemDateTime();
            hmEventLog.update();
        }
    }
    ttsCommit;
}
