public static str convertGeorgianToHijri(System.DateTime _georgianDate)
{
    System.Globalization.Calendar           Calendar = new System.Globalization.HijriCalendar();
    System.Globalization.DateTimeFormatInfo hirjiDate;
    System.Globalization.CultureInfo        cultureInfo = new System.Globalization.CultureInfo("ar-SA",false);
    str                                     dateString;

    if (year(DateTimeUtil::date(Global::CLRSystemDateTime2UtcDateTime(_georgianDate))) == 1900)
    {
        return "";
    }

    hirjiDate = cultureinfo.get_DateTimeFormat();
    hirjiDate.set_Calendar(Calendar);
    dateString = _georgianDate.ToString("dd/MM/yyyy", hirjiDate);

    return dateString;
}

public static date convertHijriToGeorgian(str _hijriDate)
{
    System.Globalization.CultureInfo arCul = new System.Globalization.CultureInfo("ar-SA");
    System.DateTime                  tempDateTime;

    str strTemp;
    str hijriDateStr;
    date georgianDate;

    hijriDateStr = subStr(_hijriDate, 0, 10);

    if (_hijriDate != '')
    {
        try
        {
            tempDateTime = System.DateTime::ParseExact(hijriDateStr, "dd/MM/yyyy", arCul, System.Globalization.DateTimeStyles::AllowWhiteSpaces);
        }
        catch
        {
            error("Unexpected Hirji date format.");
        }

        strTemp = tempDateTime.ToString("dd/MM/yyyy");
        georgianDate = str2date(strTemp, 123);
    }

    return georgianDate;

}
