class HMInsuranceBatchAssignmentUIBuilder extends SysOperationAutomaticUIBuilder
{
    DialogGroup grpCust, grpDate;
    DialogField custAccountField;
    DialogField fromDateField, toDateField;
    HMInsuranceBatchAssignmentDataContract dataContract;
}

Method.Source:
public void build()
{
    Dialog dialogLocal;

    dialogLocal = this.dialog();

    dataContract = this.dataContractObject();

    grpCust = dialogLocal.addGroup("Insurance account");
    custAccountField         = this.addDialogField(methodStr(HMInsuranceBatchAssignmentDataContract, parmCustAccount), dataContract);

    grpDate = dialogLocal.addGroup("Date");
    fromDateField            = this.addDialogField(methodStr(HMInsuranceBatchAssignmentDataContract, parmFromDate), dataContract);
    toDateField              = this.addDialogField(methodStr(HMInsuranceBatchAssignmentDataContract, parmToDate), dataContract);


}

public void lookupCustomer(FormStringControl _ctrl)
{
    Query query = new Query();
    QueryBuildDataSource qbds_CustTable;
    SysTableLookup sysTableLookup;
    QueryBuildRange qbr;
    //super();
    sysTableLookup = SysTableLookup::newParameters(tablenum(CustTable), _ctrl);
    qbds_CustTable = query.addDataSource(tableNum(CustTable));
    sysTableLookup.addLookupfield(fieldnum(CustTable, AccountNum), true);
    sysTableLookup.addLookupMethod(tableMethodStr(CustTable, name));
    qbr = qbds_CustTable.addRange(fieldNum(CustTable, CustType));
    qbr.value(SysQuery::value(CustType::Insurance));
    sysTableLookup.parmUseLookupValue(false);
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void postBuild()
{
    DialogField dialogField;
    Dialog dialogLocal = this.dialog();

    super();

    dialogField = this.bindInfo().getDialogField(this.dataContractObject(), methodStr(HMInsuranceBatchAssignmentDataContract, parmCustAccount));
    dialogField.registerOverrideMethod(methodStr(FormStringControl, lookup), methodStr(HMInsuranceBatchAssignmentUIBuilder, lookupCustomer), this);
}
