public InventDimParm parmDimParmVisibleGrid(
    InventDimParm   _dimParmVisibleGrid,
    boolean         _overRideUserSetup)
{
    InventDimParm       inventDimParmLocal;
    //InventDimCtrl_Frm   obj = new InventDimCtrl_Frm();
    overRideUserSetup = _overRideUserSetup;

    dimParmVisibleGrid = _dimParmVisibleGrid;

    dimParmVisibleGrid.InventBatchIdFlag=true;
    dimParmVisibleGrid.InventSiteIdFlag=true;
    dimParmVisibleGrid.InventLocationIdFlag=true;
    inventDimParmLocal = next parmDimParmVisibleGrid(dimParmVisibleGrid);


    return inventDimParmLocal;
}
