public static boolean isCancelaltionNoShowCode(ItemId _itemId)
{
    HMInventTable hmInventTable;

    select NoShowCancellation from hminventTable where hminventTable.InventTable == _itemId;

    if(hminventTable.NoShowCancellation == HMNoShowCancellation::None)
    {
        return false;
    }
    else
    {
        return true;
    }
}

public static boolean isConsultationItem(ItemId _itemId)
{
    if (InventTable::find(_itemId).itemGroup().ItemGroupId == HMBillParameters::find().ConsultationItemGroup)
    {
        return true;
    }
    else
    {
        return false;
    }
}

public static EcoResDescription showDescription(ItemId _itemId)
{
    return InventTable::find(_itemId).productName( CompanyInfo::languageId());
}

public static EcoResDescription showDescriptionByRecId(RecId _inventTablerecId)
{
    return InventTable::findRecId(_inventTablerecId).productName( CompanyInfo::languageId());
}

public static EcoResProductName showItemName(ItemId _itemId)
{
    return InventTable::find(_itemId).productName( CompanyInfo::languageId());
}

public static EcoResProductName showItemNameByRecId(RecId _inventTablerecId)
{
    return InventTable::findRecId(_inventTablerecId).productName( CompanyInfo::languageId());
}

public static String255 showMedicationNameStrengthForm(ItemId _itemId)
{
    String255                  medication;
    HMInventTable              hmInventTable = hmInventTable::findByItemId(_itemId);

    medication=HMInventTableClass::showDescription(_itemId) + ' ' + hmInventTable.Strength + ' ' + HMMedicationForm::find(hmInventTable.HMMedicationForm).Description;

    return medication;

}
