/// <summary>
///Returns item's procurement category
/// </summary>
[SysClientCacheDataMethodAttribute(true)]
public static display EcoResCategoryName getProcCategory(InventTable _inventTable)
{
    return EcoResCategory::find(EcoResProductCategory::findByItemIdCategoryHierarchyRole(_inventTable.ItemId, EcoResCategoryNamedhierarchyrole::Procurement).Category).Name;
}

/// <summary>
///return item's sales category
/// </summary>
[SysClientCacheDataMethodAttribute(true)]
public static display EcoResCategoryName getSalesCategory(InventTable _inventTable)
{
    return EcoResCategory::find(EcoResProductCategory::findByItemIdCategoryHierarchyRole(_inventTable.ItemId, EcoResCategoryNamedhierarchyrole::Sales).Category).Name;
}

