protected void preRunModifyContract()
{
    //define object for report contract
    HMItemizedBillReportContract contract;

    //get the reference of the current contract object
    contract = this.parmReportContract().parmRdpContract() as HMItemizedBillReportContract;

    //modify the parameter value of the contract
    //contract.parmCaseNo("00000066-000021");
    contract.parmCaseNo(HMCase::find(HMPatientChargeEntryTable::find(this.parmArgs().record().RecId).HMCase).CaseNo);
}

public static void main(Args args)
{
    HMItemizedBillReportController hmItemizedBillReportController;

    hmItemizedBillReportController = new HMItemizedBillReportController();

    //pass the caller args to the controller
    hmItemizedBillReportController.parmArgs(args);

    if (args.menuItemName() == menuitemActionStr(HMItemizedBillReport))
        hmItemizedBillReportController.parmReportName(ssrsReportStr(HMItemizedBillReport, ItemizedBillReportDesign));
    else if (args.menuItemName() == menuitemActionStr(HMItemizedBillForPatientReport))
        hmItemizedBillReportController.parmReportName(ssrsReportStr(HMItemizedBillReport, ItemizedBillForPatientDesign));
    else if (args.menuItemName() == menuitemActionStr(HMItemizedBillForInsuranceReport))
        hmItemizedBillReportController.parmReportName(ssrsReportStr(HMItemizedBillReport, ItemizedBillForInsuranceDesign));



    //execute the report
    hmItemizedBillReportController.parmShowDialog(false);
    hmItemizedBillReportController.startOperation();
}
