Method.Source:
/// <summary>
///
/// </summary>
/// <param name="args"></param>
[PostHandlerFor(classStr(JournalFormTable), methodStr(JournalFormTable, enableFieldsActive))]
public static void JournalFormTable_Post_enableFieldsActive(XppPrePostArgs args)
{
    FormCheckBoxControl         ctrlMarkUserBlock;
    FormCheckBoxControl         ctrlSystemBlocked;
    FormCheckBoxControl         ctrlMarkInUseBlock;
    JournalTableMap             journalTable;
    FormRun                     formRunLines, formRun;
    Object                      formRunObject;
    UnknownNoYes                formHasMethodEnableFieldsActive;
    JournalFormTable formTable = args.getThis();

    journalTable = args.getArg(identifierStr(journalTable));
    formHasMethodEnableFieldsActive = args.getArg(identifierStr(formHasMethodEnableFieldsActive));
    formRunLines = args.getArg(identifierStr(formRunLines));
    formRunObject = args.getArg(identifierStr(formRunObject));

    ctrlMarkUserBlock = formTable.parmCtrlMarkUserBlock();
    ctrlSystemBlocked = formTable.parmCtrlSystemBlocked();
    ctrlMarkInUseBlock = formtable.parmCtrlMarkInUseBlock();

    if(!formTable.parmMazikCareCaller())
    {
        if(journalTable.RecId != 0)
        {
            ctrlMarkUserBlock.value(journalTable.BlockUserId ? true : false);
            ctrlMarkUserBlock.allowEdit(!formRunLines && journalTable.JournalId && !journalTable.BlockUserGroupId ? true : false);

            ctrlSystemBlocked.allowEdit(journalTable.SystemBlocked ? true : false);
            ctrlMarkInUseBlock.allowEdit(journalTable.SessionId ? true : false);

            if(formHasMethodEnableFieldsActive == UnknownNoYes::Unknown)
            {
                formHasMethodEnableFieldsActive = formHasMethod(formRun, identifierStr(enableFieldsActive)) ?  UnknownNoYes::Yes : UnknownNoYes::No;
            }

            if (formHasMethodEnableFieldsActive == UnknownNoYes::Yes)
            {
                formTable.enableFieldsActive();
            }
        }

    }
}
