[PostHandlerFor(classStr(LedgerJournalTransUpdateCust), methodStr(LedgerJournalTransUpdateCust, updateNow))]
public static void LedgerJournalTransUpdateCust_Post_updateNow(XppPrePostArgs args)
{
    LedgerJournalTrans _ledgerJournalTrans;
    CustTrans          custTrans;
    LedgerJournalTransUpdateCust ledgerJournalTransUpdCust = args.getThis();

    _ledgerJournalTrans = args.getArg(identifierStr(_ledgerJournalTrans));
    custTrans = args.getArg(identifierStr(custTrans));

    if(!_ledgerJournalTrans.CustTransId)
    {
        if(_ledgerJournalTrans.HMAppointmentNumber || _ledgerJournalTrans.HMMerchantDetails
            || _ledgerJournalTrans.HMCase)
        {
            custTrans.selectForUpdate(true);
            ttsbegin;
            custTrans.HMAppointmentNumber = _ledgerJournalTrans.HMAppointmentNumber;
            custTrans.HMMerchantDetails = _ledgerJournalTrans.HMMerchantDetails;
            custTrans.PaymentNotes      = _ledgerJournalTrans.PaymentNotes;
            custTrans.HMCase            = _ledgerJournalTrans.HMCase;
            custTrans.update();
            ttscommit;
        }
    }
}
