protected void preRunModifyContract()
{
    FormRun formRun;
    HMMedicationLabelDataContract       contract;
    HMResourceSchedulingAppointment     hmResourceSchedulingAppointment;
    str                                 caseTransRecIds;
    Map                                 mapService;
    MapEnumerator                       mapEnumerator;

    contract = this.parmReportContract().parmRdpContract() as HMMedicationLabelDataContract;


    super();

    contract.parmAppointmentRecId(this.parmArgs().record().RecId);
    formRun = this.parmArgs().caller();
    if(formRun)
    {
        if(formHasMethod(formRun,identifierStr(getPerformedServiceMap)))
        {
            mapService = this.parmArgs().caller().getPerformedServiceMap();
            mapEnumerator = mapService.getEnumerator();
            while(MapEnumerator.moveNext())
            {
                caseTransRecIds += mapEnumerator.currentKey() + ',';
            }
            contract.parmCaseTransRecIds(caseTransRecIds);
        }
    }


}

