class HMNumberSequenceGeneratorClass
{
    NumberSequenceReference         appointmentOrderNumberSeqReference;//Appointment order No
    NumberSequenceTable             appointmentOrderNumberSeqTable;//Appointment order No
    NumberSeq                       appointmentOrderNumberSeq;//Appointment order no
}

Method.Source:
public Num getNewNumSeqForAppointmentNum(RecId _operatingUnit)
{
    Num                     number;


    appointmentOrderNumberSeqReference  = HMParameters::numRefAppointmentNumber(_operatingUnit);
    appointmentOrderNumberSeqTable      = NumberSequenceTable::find(appointmentOrderNumberSeqReference.NumberSequenceId);

    if(appointmentOrderNumberSeqTable)
    {
        // generate Appointment order number if number sequence code is created.
        appointmentOrderNumberSeq = NumberSeq::newGetNumFromId(appointmentOrderNumberSeqTable.RecId, true, false, null, false);

        if(appointmentOrderNumberSeq)
        {
            number = appointmentOrderNumberSeq.num();
        }
    }

    return number;
}
