class HMNumberSequenceHandler
{
    NumberSequenceReference         genericOrderNumberSeqReference;//Generic order No
    NumberSequenceTable             genericOrderNumberSeqTable;//Generic order No
    NumberSeq                       genericOrderNumberSeq;//Generic order no
}

Method.Source:
public Num generateNumberSequence(RecId _operatingUnitRecId,ExtendedTypeId _enumName)
{
    SysLastValue            nextNumSeq;
    Num                     number;

    genericOrderNumberSeqReference  = NumberSeqReference::findReference(_enumName, NumberSeqScopeFactory::createOperatingUnitScope(_operatingUnitRecId));//HMParameters::numRefAppointmentNumber(_operatingUnitRecId);
    genericOrderNumberSeqTable      = NumberSequenceTable::find(genericOrderNumberSeqReference.NumberSequenceId);

    if(genericOrderNumberSeqTable)
    {

        genericOrderNumberSeq = NumberSeq::newGetNumFromId(genericOrderNumberSeqTable.RecId, true, true, nextNumSeq, true);

        if(genericOrderNumberSeq)
        {
            [number] = nextNumSeq.value;
        }
    }

    return number;
}

public Num generateNumberSequenceDataArea(ExtendedTypeId _enumName)
{
    SysLastValue            nextNumSeq;
    Num                     number;

    genericOrderNumberSeqReference  = NumberSeqReference::findReference(_enumName);
    genericOrderNumberSeqTable      = NumberSequenceTable::find(genericOrderNumberSeqReference.NumberSequenceId);

    if(genericOrderNumberSeqTable)
    {

        genericOrderNumberSeq = NumberSeq::newGetNumFromId(genericOrderNumberSeqTable.RecId, true, true, nextNumSeq, true);

        if(genericOrderNumberSeq)
        {
            [number] = nextNumSeq.value;
        }
    }

    return number;
}
