    approvalThreshold = _approvalThreshold;
    return approvalThreshold;
}

public boolean validate()
{
    boolean isValid = true;

    if (!fromDate)
    {
        isValid = checkFailed("From Date must be filled");
    }

    if (!toDate)
    {
        isValid = checkFailed("To Date must be filled");
    }

    if (fromDate && toDate && fromDate > toDate)
    {
        isValid = checkFailed("From Date cannot be greater than To Date");
    }

    if (numberOfThreads <= 0)
    {
        isValid = checkFailed("Number of threads must be greater than 0");
    }

    if (autoApprove && approvalThreshold <= 0)
    {
        isValid = checkFailed("Approval threshold must be greater than 0 when auto approve is enabled");
    }

    return isValid;
}
