public static void main(Args _args)
{
    HMPaperClaimController controller;
    controller = HMPaperClaimController::newFromArgs(_args);
    controller.startOperation();
}

public static HMPaperClaimController newFromArgs(Args _args)
{
    HMPaperClaimController    controller;
    HMPaperClaimContract      dataContract;

    controller = new HMPaperClaimController();
    controller.initializeFromArgs(_args);

    dataContract = controller.getDataContractObject('_dataContract');

    dataContract.parmFromDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    dataContract.parmToDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));

    return controller;
}
