public void build()
{
    Dialog      dialogLocal = this.dialog();
    HMPaperClaimContract contract = this.dataContractObject();

    //dialogLocal.addGroup("@SYS40");
    this.addDialogField(methodStr(HMPaperClaimContract,parmFromDate), contract);
    this.addDialogField(methodStr(HMPaperClaimContract,parmToDate), contract);

    //dialogLocal.addGroup("@MAZ96");
    this.addDialogField(methodStr(HMPaperClaimContract,parmInsuranceCarrier), contract);

    //dialogLocal.addGroup("@MAZ65");
    this.addDialogField(methodStr(HMPaperClaimContract,parmPhysician), contract);
}

public void getFromDialog()
{
    HMClaimGenerationReportContract contract = this.dataContractObject();
    super();
}

public void initializeFields()
{
    HMClaimGenerationReportContract contract = this.dataContractObject();
}

public void lookup(FormStringControl    _control)
{
    SysTableLookup sysTablelookup;
    Query query = new Query();

    query.addDataSource(tableNum(HMResourceMaster));

    sysTablelookup = SysTableLookup::newParameters(tableNum(HMResourceMaster),_control);
    sysTablelookup.addLookupfield(fieldNum(HMResourceMaster,Code));
    sysTablelookup.addLookupfield(fieldNum(HMResourceMaster, Description));


    sysTablelookup.parmQuery(query);
    sysTablelookup.performFormLookup();

}

public void lookupIns(FormStringControl    _control)
{
    Query query = new Query();
    SysTableLookup sysTablelookup;

    query.addDataSource(tableNum(HMInsuranceCarrier), "", UnionType::None, true);
    query.dataSourceTable(tableNum(HMInsuranceCarrier)).fields().addField(fieldNum(HMInsuranceCarrier, Code));
    query.dataSourceTable(tableNum(HMInsuranceCarrier)).fields().addField(fieldNum(HMInsuranceCarrier, Name));


    sysTablelookup = SysTableLookup::newParameters(tableNum(HMInsuranceCarrier),_control);
    sysTablelookup.addLookupfield(fieldNum(HMInsuranceCarrier,Code));
    sysTablelookup.addLookupfield(fieldNum(HMInsuranceCarrier, Name));


    sysTablelookup.parmQuery(query);
    sysTablelookup.performFormLookup();

}

public void postBuild()
{
    DialogField dialogDateFrom;
    DialogField dialogDateTo;
    DialogField dialogPhysician;
    DialogField dialogIns;

    super();

    // get dialog fields
    dialogDateFrom = this.bindInfo().getDialogField(this.dataContractObject(),
                methodStr(HMPaperClaimContract, parmFromDate));
    dialogDateTo = this.bindInfo().getDialogField(this.dataContractObject(),
                methodStr(HMPaperClaimContract, parmToDate));

    dialogPhysician = this.bindInfo().getDialogField(this.dataContractObject(),
                methodStr(HMPaperClaimContract, parmPhysician));

    dialogPhysician.registerOverrideMethod(
          methodStr(FormStringControl, lookup),
          methodStr(HMPaperClaimUIBuilder,lookup),
          this);

    dialogPhysician.visible(false);

    dialogIns = this.bindInfo().getDialogField(this.dataContractObject(),
                methodStr(HMClaimGenerationReportContract, parmInsuranceCarrier));

    dialogIns.registerOverrideMethod(methodStr(FormStringControl, lookup),
          methodStr(HMPaperClaimUIBuilder,lookupIns),
          this);

    if (dialogIns)
    {
        dialogIns.lookupButton(2);
    }
}

public void postRun()
{
    HMPaperClaimContract contract = this.dataContractObject();
}
