protected void prePromptModifyContract()
{
    HMCase hmcase;
    HMPatientAssignmentBenefitsContract contract;
    FormDataSource fds;

    contract = this.parmReportContract().parmRdpContract() as HMPatientAssignmentBenefitsContract;
    hmcase = Args.record();
    fds = FormDataUtil::getFormDataSource(Args.record());
    contract.parmCaseNo(hmcase.CaseNo);
    this.parmShowDialog(false);
}

public static client void main(Args args)
{
    HMPatientAssignmentBenefitsController hMPatientAssignmentBenefitsController;

    hMPatientAssignmentBenefitsController = new HMPatientAssignmentBenefitsController();

    hMPatientAssignmentBenefitsController.parmArgs(args);

    hMPatientAssignmentBenefitsController.parmReportName(ssrsReportStr(HMPatientAssignmentBenefits,Report));

    hMPatientAssignmentBenefitsController.startOperation();

}
