public void selectionChanged()
{
    HMPatientEODStatement    HMPatientEODStatement;
    super();

    HMPatientEODStatement = this.listPage().activeRecord('HMPatientEODStatement');

    if(HMPatientEODStatement.Status == HMPatientEODStatementStatus::Open)
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_CalcStmt), true);
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_PostStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_ClearStmt), false);
    }
    else if(HMPatientEODStatement.Status == HMPatientEODStatementStatus::Calculated)
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_CalcStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_PostStmt), true);
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_ClearStmt), true);
    }
    else if(HMPatientEODStatement.Status == HMPatientEODStatementStatus::Posted)
    {
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_CalcStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_PostStmt), false);
        this.listPage().actionPaneControlEnabled(formControlStr(HMPatientEODStatementListPage,HMPatientEODStatementClass_ClearStmt), false);
    }
}
