public void createEligibilityLog(RecId _patientRecId, RecId _patientInsuranceRecId, HMInsuranceMembershipId _membershipId,
                            NoYes _eligible, RecId _bypassReasonRecId)
{
    HMPatientEligibilityLog     hmPatientEligibilityLog;

    try
    {
        hmPatientEligibilityLog.initValue();

        hmPatientEligibilityLog.HMPatient           = _patientRecId;
        hmPatientEligibilityLog.EligibilityStatus   = _eligible;
        hmPatientEligibilityLog.MembershipId        = _membershipId;
        hmPatientEligibilityLog.ReasonTableEligibilityBypass           = _bypassReasonRecId;
        hmPatientEligibilityLog.HMPatientInsurance           = _patientInsuranceRecId;

        hmPatientEligibilityLog.insert();
    }
    catch
    {
        error("Error creating eligibility log");
    }
}

public RecId getInsuranceByproviderid(HMProviderID _providerId)
{
    HMInsuranceCarrier      hmInsuranceCarrier;
    HMPatientInsurance      hmPatientInsurance;
    CustTable               custTable;

    select RecId from hmInsuranceCarrier
        where hmInsuranceCarrier.HMProviderID == _providerId
        join recId from custTable
            where custTable.RecId == hmInsuranceCarrier.CustTable
        join RecId from hmPatientInsurance
            where hmPatientInsurance.InsuranceCarrier == custTable.AccountNum;

    return hmPatientInsurance.RecId;
}
