protected void prePromptModifyContract()
{
    HMCase hmcase;
    HMPatientFactSheetsContract contract;
    FormDataSource fds;

    contract = this.parmReportContract().parmRdpContract() as HMPatientFactSheetsContract;
    hmcase = Args.record();
    fds = FormDataUtil::getFormDataSource(Args.record());
    contract.parmCaseNo(hmcase.CaseNo);
    this.parmShowDialog(false);
}

public static client void main(Args args)
{
    HMPatientFactSheetsController hMPatientFactSheetsController;

    hMPatientFactSheetsController = new HMPatientFactSheetsController();

    hMPatientFactSheetsController.parmArgs(args);

    hMPatientFactSheetsController.parmReportName(ssrsReportStr(HMPatientFactSheets,Report));

    hMPatientFactSheetsController.startOperation();

}
