Method.Source:
/// <summary>
///    Called by the menu items.
/// </summary>
/// <param name="_args">
///    The <c>Args</c> object passed from the menu item.
/// </param>
public static void main(Args _args)
{
    FormRun     insuranceForm;

    _args.name(formstr(HMPatientInsurance));

    if(_args.openMode() != OpenMode::New)
    {
        _args.lookupRecord(_args.record());
    }
    else
    {
        _args.record(null);
    }
    insuranceForm = classfactory.formRunClass(_args);

    if (insuranceForm)
    {
        insuranceForm.init();
        insuranceForm.run();
        insuranceForm.wait();
    }
}
