protected void preRunModifyContract()
{
    HMPatientLabelDataContract contract;
    HMResourceSchedulingAppointment     hmResourceSchedulingAppointment;

    contract = this.parmReportContract().parmRdpContract() as HMPatientLabelDataContract;

    if(contract.parmLabelType() == HMPatientLabelType::MRFolder)
    {
        this.parmReportContract().parmReportName(ssrsReportStr(HMPatientLabel,MRFolder));
    }
    else if(contract.parmLabelType() == HMPatientLabelType::Wrist)
    {
        this.parmReportContract().parmReportName(ssrsReportStr(HMPatientLabel,Wrist));
    }
    else if(contract.parmLabelType() == HMPatientLabelType::PatientVisit)
    {
        this.parmReportContract().parmReportName(ssrsReportStr(HMPatientLabel,PatientVisit));
    }

    super();

    contract.parmAppointmentRecId(this.parmArgs().record().RecId);
}

public static client void main(Args args)
{
    HMResourceSchedulingAppointment appt;

    //define the new object for controller class
    HMPatientLabelReportController hmPatientLabelReportController;

    hmPatientLabelReportController = new HMPatientLabelReportController();

    //pass the caller args to the controller
    hmPatientLabelReportController.parmArgs(args);
    hmPatientLabelReportController.parmReportName(ssrsReportStr(HMPatientLabel,MRFolder));

    //execute the report
    hmPatientLabelReportController.startOperation();
}
