private CustAccount getMRNFromPatientAppointment(Common _common)
{
    HMResourceSchedulingAppointment hmResourceSchedulingAppointment;

    select HMPatient from hmResourceSchedulingAppointment where hmResourceSchedulingAppointment.RecId == _common.RecId;

    return hmResourceSchedulingAppointment.getPatientChartNum();
}

public CustAccount getPatientMRN(Common _common)
{
    CustAccount mrnNo;

    switch(_common.TableId)
    {
        case tableNum(HMResourceSchedulingAppointment):
            mrnNo = this.getMRNFromPatientAppointment(_common);
            break;
    }

    return mrnNo;
}
