]
class HMPatientNameAndAddressDP extends SRSReportDataProviderBase
{
    HMPatientNameAndAddressTmp  HMPatientNameAndAddressTmp;
}

Method.Source:
[SRSReportDataSetAttribute("PatientNameAndAddressTmp")]
public HMPatientNameAndAddressTmp getPatientNameAndAddressTmp()
{
    select * from HMPatientNameAndAddressTmp;
    return HMPatientNameAndAddressTmp;
}

public void processReport()
{
    AccountNum accountNum;
    HMPatientNameAndAddressDataContract dataContract;
    Query query;
    QueryRun queryRun;
    CustTable               custTable;
    QueryBuildDataSource queryBuildDataSource;
    str                     patientAddress;
    query = new Query(queryStr(HMPatientNameAndAddressReportQuery));
    dataContract = this.parmDataContract();
    accountNum = dataContract.parmPatientAccount();
    patientAddress = dataContract.parmLogisticsAddressing();
    queryBuildDataSource = query.dataSourceTable(tablenum(CustTable));

    if (accountNum)
    {
        queryBuildDataSource.addRange(fieldnum(CustTable, AccountNum)).value(SysQuery::value(accountNum));
    }

    queryRun = new QueryRun(query);
    while(queryRun.next())
    {
        HMPatientNameAndAddressTmp.clear();
        custTable = queryRun.get(tablenum(custTable));
        HMPatientNameAndAddressTmp.PatientMRN = custTable.AccountNum;
        HMPatientNameAndAddressTmp.PatientName = custTable.name();
        HMPatientNameAndAddressTmp.PatientAddress = patientAddress;
        HMPatientNameAndAddressTmp.insert();
    }

}
